export function optionallyAddLeadingSlash(filePath) {
    if (filePath.startsWith('/') || filePath.startsWith('https://')) {
        return filePath;
    }
    return '/' + filePath;
}
export function optionallyRemoveLeadingSlash(filePath) {
    if (filePath.startsWith('/')) {
        return filePath.slice(1);
    }
    return filePath;
}
